/* eslint-disable */
<template>
  <div id="all" class="all">
  <div id = "Title" class="Title">
    <h4>Wordle</h4>
  </div>
  <div id="Wordle" class="Wordle">
  </div>
</div>
</template>
<style type="text/css">
.All{ position:absolute;left:10px;top:0px;z-index:0;width:300px;height:700px} 
.Title{ position:absolute;left:10px;top:100px;z-index:30;width:100px;height:50px} 
/* div背景色为红色 */ 
.Wordle{ position:absolute;left:0px;top:100px;z-index:50;width:300px;height:600px} 
</style>

<script>
import * as d3 from "d3";
import * as d3Chromatic from 'd3-scale-chromatic';
export default {
  name: "App",
  data(){
    return{
      svg3:undefined
    }
  },
  //created(){
    //Bus.$on("Map_place", (val) => {
                //this.place = val;}),
    //Bus.$on("Map_places", (val) => {
                //this.places = val;})},
  mounted() {
    this.generateWord()
  },
  methods: {
    generateWord() {

// set the dimensions and margins of the graph
var margin = {top: 35, right: 5, bottom: 40, left: 30},
    width = 300 - margin.left - margin.right,
    height = 700 - margin.top - margin.bottom;

// append the svg object to the body of the page
var svg = d3.select("#Wordle")
  .append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
  .append("g")
    .attr("transform",
          "translate(" + margin.left + "," + margin.top + ")");


// Parse the Data
d3.csv("sushi_wordcount.csv", function(data) {

data.sort(function(b, a) {
  return a.count - b.count;
});
var data_1 = data.slice(0,30)
var myColor = d3.scaleSequential()
    .domain([0.79,0.45])
    .interpolator(d3Chromatic.interpolateRdYlBu);
for (var i = 0; i<5; i++) {
  svg.append("circle").attr("cx",function(){return 130+i*25}).attr("cy",0).attr("r", 6).style("fill", function(){return myColor(0.45+i*(0.79-0.45)/5)})
}
for (i = 0; i<5; i++) {
  svg.append("text").attr("x",function(){return 121+i*25}).attr("y",20)
  .text(function(){return (0.45+i*(0.79-0.45)/5).toFixed(2)})
}


// Add X axis
var x = d3.scaleLinear()
  .domain([0, 130])
  .range([ 0, width]);
svg.append("g")
  .attr("transform", "translate(0," + height + ")")
  .call(d3.axisBottom(x))
  .selectAll("text")
    .attr("transform", "translate(-10,0)rotate(-45)")
    .style("text-anchor", "end");

// Y axis
var y = d3.scaleBand()
  .range([ 25, height ])
  .domain(data_1.map(function(d) { return d.word; }))
  .padding(1);
svg.append("g")
  .call(d3.axisLeft(y))

// Lines
svg.selectAll("myline")
  .data(data_1)
  .enter()
  .append("line")
    .attr("x1", function(d) { return x(d.count); })
    .attr("x2", x(0))
    .attr("y1", function(d) { return y(d.word); })
    .attr("y2", function(d) { return y(d.word); })
    .attr("stroke", "grey")

// Circles
svg.selectAll("mycircle")
  .data(data_1)
  .enter()
  .append("circle")
    .attr("cx", function(d) { return x(d.count); })
    .attr("cy", function(d) { return y(d.word); })
    .attr("r", "7")
    .style("fill", function(d) { 
                var value = d.senti ;
        return myColor(value); })
    //.style("fill", "#6495ED")
    .attr("stroke", "black")
    .on("mouseover", function(d){return mouseover(d),console.log("here")})
    .on("mousemove", function(d){return tooltip.style("top",
    (d3.event.pageY-10)+"px").style("left",(d3.event.pageX+10)+"px").attr("data-html", "true").html('Word:'+d.word+'<br/>'+'Count:'+d.count+'  Poet:'+d.name+'<br/>'+'Senti:'+d.senti)})
    .on("mouseout", function(){return tooltip.style("visibility", "hidden")})
});
var tooltip = d3.select("#Wordle")
    .append("div")
    .style("width",'100px')
    .style("position", "absolute")
    .style("z-index", "10")
    .style("visibility", "hidden")
     .style("border", "solid")
    .style("border-width", "1px")
    .style("border-radius", "5px")
    .style("padding", "10px")
let self = this
function mouseover(d){
	tooltip.style("visibility", "visible")
	.html('Word:'+d.word+'<br/>'+'Count:'+d.count+'  Poet:'+d.name+'<br/>'+'Senti:'+d.senti)
	d3.select("#Wordle")
	.on('mouseover', function(){self.GLOBAL.Log_file.push(['timestamp',new Date().getTime()/1000,'hover-wordle-position',d3.mouse(this),d.word,'\n']); })
}


}}
}

</script>
